SELECT menu_id
FROM sys_perms_auth
WHERE user_id = #{userId}

SELECT a.id, a.menu_id AS parentId, a.title AS name
FROM sys_element a
         LEFT JOIN sys_perms_auth b ON a.id = b.menu_id
WHERE b.user_id = #{userId}
  AND a.menu_id = #{menuId}
ORDER BY a.order_num ASC
